userDefinedLinkHintCss:
div > .vimiumHintMarker {
  /* Link hint boxes */
  background: #5294e2;
  border: none;
  border-radius: 0px;
  box-shadow: 0px 2px 1px rgba(0, 0, 0, 0.4);
}

div > .vimiumHintMarker span {
  /* Link hint text */
  //text-transform: lowercase;
  font: Ubuntu\ Mono;
  font-size: 12px;
  color: #ffffff;
  text-shadow: none;
}

div > .vimiumHintMarker > .matchingCharacter {
  /* Link hint matching characters */
  color: #cccccc;
}
